import { useState } from 'react'

const StatistciLine = (props) => {
  return (
    <div>

    </div>
  )
}

const Statistics = (props) => {
  if (props.all.length == 0) {
  return (
  <div>
    No feedback given
  </div>
  )
  }

  const sum = props.all.reduce((sum, value) => {
    return sum + value
  })

  return (
    <div>
      <p>good {props.good}</p>
      <p>neutral {props.neutral}</p>
      <p>bad {props.bad}</p>      
      <p>all {props.all.length}</p>
      <p>average {sum/props.all.length}</p>
      <p>positive {(props.good/props.all.length) * 100} %</p>
    </div>
  )
}
const Button = ({handleClick, text}) => (
  <button onClick={handleClick}>{text}</button>
)

const App = () => {
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState([])

  const handleGoodClick = () => {
    setAll(all.concat(1))
    setGood(good + 1)
  }

  const handleBadClick = () => {
    setAll(all.concat(-1))
    setBad(bad + 1)
  }

  const handleNeutralClick = () => {
    setAll(all.concat(0))
    setNeutral(neutral + 1)
  }

  return (
    <div>
      <h1>give feedback</h1>
      <Button handleClick={handleGoodClick} text="good"/>
      <Button handleClick={handleNeutralClick} text="neutral"/>
      <Button handleClick={handleBadClick} text="bad"/>
      <h1>statistics</h1>
      <StatisticLine value={good} text="good"/>
      <StatisticLine value={neutral} text="neutral"/>
      <StatisticLine value={bad} text="bad"/>
      <StatisticLine value={all} text="all"/>
      <StatisticLine value={all} text="average"/>
      <StatisticLine value={all} text="positive"/>



    </div>
  )
}

export default App
