import { useState, useEffect } from 'react'
import axios from 'axios'
import Filter from './components/Filter'
import matchers from '@testing-library/jest-dom/matchers'


const CountryList = ({countries, showCountryHandler}) => {
  return (
    <div>
        {countries.map(country => 
          <p key={country.name.common} >
          {country.name.common} <button onClick={(event) => showCountryHandler(country.name.common)} >Show</button>
          </p>
          )}
    </div>
  )
}

const Country = ({country}) => {

  useEffect(() => {
    axios
    .get('http://api.weatherstack.com/')
    .then(response => {
      setCountries(response.data)
    })
  }, [])

  return (
    <div>
      <Info key={country.name.common} country={country}/>
      <Languages key={country.name.common} languages={Object.values(country.languages)}/>
      <img src={country.flags.png} style={{ width: '50%' }}></img>
    </div>
  )
}

const Info = ({country}) => {
  return (
    <div>
      <h2>{country.name.common}</h2>
      <p>capital {country.capital}</p>
      <p>area {country.area}</p>
    </div>
  )
}

const Languages = ({languages}) => {
  return (
    <div>
      <p><strong>languages:</strong></p>
      <ul>
        {languages.map(language =>
          <li key={language}>
            {language}
          </li>
        )}
      </ul>
    </div>
  )
}

const App = () => {
  const [countries, setCountries] = useState([]) 
  const [searched, setSearched] = useState('')
  const api_key = process.env.REACT_APP_API_KEY

  useEffect(() => {
    axios
    .get('https://restcountries.com/v3.1/all')
    .then(response => {
      setCountries(response.data)
    })
  }, [])

  const handleSearch = (event) => {    
    setSearched(event.target.value)    

  }

  const handleShowCountry = (country) => {
    setSearched(country)
  }

  const countriesToShow = countries.filter((country) => country.name.common.toLocaleLowerCase().includes(searched.toLocaleLowerCase()));

  const showCountries = () => {
    if (countriesToShow.length > 10 ) {
      return (
        <div>Too many matches, specify another filter</div>
      )
    }

    if (countriesToShow.length == 1) {
      return (
        <Country key={countriesToShow[0].name.common} country={countriesToShow[0]}/>
      )
    }

    return (
        <CountryList countries={countriesToShow} showCountryHandler={handleShowCountry}/>
    )
  }

  return (

    <div>
      <Filter searched={searched} handleSearch={handleSearch}/>
      {showCountries()}     
    </div>

  )

}

export default App
