import { useState, useEffect } from 'react'
import axios from 'axios'
import Filter from './components/Filter'
import matchers from '@testing-library/jest-dom/matchers'


const CountryList = ({countries, showCountryHandler}) => {
  return (
    <div>
      <ul>
        {countries.map(country => 
          <p>
          {country.name.common} <button onClick={(event) => showCountry(country)}>Show</button>
          </p>
          )}
      </ul>      
    </div>
  )
}

const Country = ({country}) => {
  return (
    <div>
      <Info key={country.name.common} country={country}/>
      <Languages key={country.name.common} languages={Object.values(country.languages)}/>
      {country.flag}
    </div>
  )
}

const Info = ({country}) => {
  return (
    <div>
      <h2>{country.name.common}</h2>
      <p>capital {country.capital}</p>
      <p>area {country.area}</p>
    </div>
  )
}

const Languages = ({languages}) => {
  return (
    <div>
      <p><strong>languages:</strong></p>
      <ul>
        {languages.map(language =>
          <li key={language}>
            {language}
          </li>
        )}
      </ul>
    </div>
  )
}

const App = () => {
  const [countries, setCountries] = useState([]) 
  const [searched, setSearched] = useState('')

  useEffect(() => {
    axios
    .get('https://restcountries.com/v3.1/all')
    .then(response => {
      setCountries(response.data)
    })
  }, [])

  const handleSearch = (event) => {    
    setSearched(event.target.value)    

  }

  const showCountry = (country) => {
    setCountries(country)
  }

  const countriesToShow = countries

  const showCountries = () => {
    if (countriesToShow.length > 10 ) {
      return (
        <div>Too many matches, specify another filter</div>
      )
    }

    if (countriesToShow.length == 1) {
      return (
        <Country country={countriesToShow[0]}/>
      )
    }

    return (
        <CountryList countries={countriesToShow} showCountryHandler={showCountry}/>
    )
  }

  return (

    <div>
      <Filter searched={searched} handleSearch={handleSearch}/>
      {showCountries()}     
    </div>

  )

}

export default App
