import { useState, useEffect } from 'react'
import axios from 'axios'

const Countries = ({countries}) => {

  if (countries.length > 10) {
    return (
      <p>
      Too many matches, specify another filter
      </p>
    )
  }

  if (countries.length == 1) {
    return (
      <p>
      Too many matches, specify another filter
      </p>
    )
  }

  return (
    <div>
      {countries.map(country => 
      <CountryList key={country.name.common} country={country}/>
      )}
    </div>
  )
}

const CountryList = ({country}) => {
  return (
    <div>
      <p>
      {country.name.common}
      </p>
    </div>
  )
}

const App = () => {
  const [countries, setCountries] = useState([]) 
  const [searched, setSearched] = useState('')

  useEffect(() => {
    axios
    .get('https://restcountries.com/v3.1/all')
    .then(response => {
      setCountries(response.data)
    })
  }, [])

  const handleSearch = (event) => {    
    setSearched(event.target.value)    

  }

  const countriesToShow = searched === '' ? countries : countries.filter((country) => country.name.common.toLocaleLowerCase().includes(searched.toLocaleLowerCase()));

  return (

    <div>
      find countries <input value={searched}
      onChange={handleSearch}
      />
      <Countries countries={countriesToShow}/>      
    </div>

  )

}

export default App
