import { useState, useEffect } from 'react'
import axios from 'axios'
import Filter from './components/Filter'
import matchers from '@testing-library/jest-dom/matchers'

const Country = ({country}) => {
  return (
    <div>
      <Info key={country.name.common} country={country}/>
      <Languages key={country.name.common} languages={Object.values(country.languages)}/>
      {country.flag}
    </div>
  )
}

const Info = ({country}) => {
  return (
    <div>
      <h2>{country.name.common}</h2>
      <p>capital {country.capital}</p>
      <p>area {country.area}</p>
    </div>
  )
}

const Languages = ({languages}) => {
  return (
    <div>
      <p><strong>languages:</strong></p>
      <ul>
        {languages.map(language =>
          <li key={language}>
            {language}
          </li>
        )}
      </ul>
    </div>
  )
}

const showCountry = (country) => {

}

const CountryList = ({country}) => {
  return (
    <div>
      <p>
      {country.name.common} <button onClick={(event) => showCountry(country)}>Show</button>
      </p>
    </div>
  )
}

const App = () => {
  const [countries, setCountries] = useState([]) 
  const [searched, setSearched] = useState('')

  useEffect(() => {
    axios
    .get('https://restcountries.com/v3.1/all')
    .then(response => {
      setCountries(response.data)
    })
  }, [])

  const showCountries = () => {

    if (countries.length > 10 ) {
      return (
        <p>Too many matchers, specify another filter</p>
      )
    }

    if (countries.length == 1) {
      return (
        <Country key={countries[0].name.common} country={countries[0]}/>
      )
    }

    return (
      <div>
        {countries.map(country => 
        <CountryList key={country.name.common} country={country}/>
        )}
      </div>
    )
  }

  const handleSearch = (event) => {    
    setSearched(event.target.value)    

  }

  const countriesToShow = searched === '' ? countries : countries.filter((country) => country.name.common.toLocaleLowerCase().includes(searched.toLocaleLowerCase()));

  return (

    <div>
      <Filter searched={searched} handleSearch={handleSearch}/>
      {showCountries()}     
    </div>

  )

}

export default App
