import { useState } from 'react'
import Filter from './components/Filter'

const Persons = ({person}) => {
  return (
    <div>
      <p>
      {person.name} {person.number}
      </p>
    </div>
  )
}

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas',
      number: "0401234567"
    },
    { name: 'Kisu Kissanen',
      number: "0407654321"
    }
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [searched, setSearched] = useState('')

  const addNew = (event) => {
    event.preventDefault()
    const person = {
      name: newName,
      number: newNumber
    }

    if (persons.some((person) => person.name === newName)) {
      alert(`${newName} is already added to phonebook`)
      return
    }
    setPersons(persons.concat(person))    
    setNewName('')
    setNewNumber('')
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleSearch = (event) => {    
    setSearched(event.target.value)    

  }

  const personsToShow = searched === '' ? persons : persons.filter((person) => person.name.toLocaleLowerCase().includes(searched.toLocaleLowerCase()));

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter searched={searched} nameHandler={handleSearch}/>

      <h2>add a new</h2>
      <Add name={newName} nameHandler={handleNameChange} number={newNumber} numberHandler={handleNumberChange}/>
      <form onSubmit={addNew}>
        <div>
          name: <input value={newName}
          onChange={handleNameChange}
          />
        </div>
        <div>
          number: <input value={newNumber}
          onChange={handleNumberChange}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
        {personsToShow.map(person => 
        <Persons key={person.name} person={person}/>
      )}


    </div>
  )

}

export default App
