import { createSlice } from '@reduxjs/toolkit'

const initialState = ''

const filterSlice = createSlice({
  name: 'filter',
  initialState,
  reducers: {
      showWithFilter(state, action) {
          const anecdotes = action.payload.anecdotes
          const searched = action.payload.content
          console.log(anecdotes)
          console.log(searched)
          const anecdotesToShow = searched === ''
              ? anecdotes
              : anecdotes
                  .filter((anecdote) => anecdote.content
                      .toLocaleLowerCase()
                      .includes(searched
                          .toLocaleLowerCase()));

      },

      otherFilter(state) {
          return ''
      }
  }
})

export const { showWithFilter, otherFilter } = filterSlice.actions
export default filterSlice.reducer
