import { useSelector, useDispatch } from 'react-redux'
import { useEffect } from 'react'
import { vote } from '../reducers/anecdoteReducer'
import anecdoteService from '../services/anecdotes'
import { setAnecdotes } from '../reducers/anecdoteReducer'
import { setVoteNotification, setNullNotification } from '../reducers/notificationReducer'

const AnecdoteList = (props) => {
    const dispatch = useDispatch()
    useEffect(() => {
      anecdoteService
        .getAll().then(anecdotes => dispatch(setAnecdotes(anecdotes)))
    }, [dispatch])

    setTimeout(() => {
      dispatch(setNullNotification(null))
    }, 1000)
    const anecdotes = useSelector(state => state.anecdotes).sort((a, b) => b.votes - a.votes)

    const voteAction = (anecdote, id) => {
        dispatch(vote(id))
        dispatch(setVoteNotification(`you voted ${anecdote}`))
        setTimeout(() => {
          dispatch(setNullNotification(null))
        }, 1000)
    }

    return (
      <div>
      {anecdotes.map(anecdote =>
        <div key={anecdote.id}>
          <div>
            {anecdote.content}
          </div>
          <div>
            has {anecdote.votes}
            <button onClick={() => voteAction(anecdote.content, anecdote.id)}>vote</button>
          </div>
        </div>
      )}
      </div>
      )
}

export default AnecdoteList
