import { useSelector, useDispatch } from 'react-redux'
import { vote } from '../reducers/anecdoteReducer'
import { setVoteNotification, setNullNotification } from '../reducers/notificationReducer'

const AnecdoteList = () => {
    const dispatch = useDispatch()
    let anecdotes = useSelector(state => state.anecdotes)
    const filter = useSelector(state => state.filter)

    anecdotes = anecdotes.filter(a => {
      const filtered = a.content.toLowerCase().includes(filter.toLowerCase())
      if (filtered) {
        return a
      }
    })

    setTimeout(() => {
      dispatch(setNullNotification(null))
    }, 1000)

    const voteAction = (anecdote, id) => {
        dispatch(vote(id))
        dispatch(setVoteNotification(`you voted ${anecdote}`))
        setTimeout(() => {
          dispatch(setNullNotification(null))
        }, 1000)
    }

    return (
      <div>
      {anecdotes.sort((a, b) => b.votes - a.votes).map(anecdote =>
        <div key={anecdote.id}>
          <div>
            {anecdote.content}
          </div>
          <div>
            has {anecdote.votes}
            <button onClick={() => voteAction(anecdote.content, anecdote.id)}>vote</button>
          </div>
        </div>
      )}
      </div>
      )
}

export default AnecdoteList
