import { createSlice } from '@reduxjs/toolkit'

const initialState = ''

const filterSlice = createSlice({
  name: 'filter',
  initialState,
  reducers: {
      showAll(state, action) {
          return action.payload
      },

      filter(state) {
          return ''
      }
  }
})

export const { showAll, filter } = filterSlice.actions
export default filterSlice.reducer
