import { createSlice } from '@reduxjs/toolkit'

const initialState = ''

const filterSlice = createSlice({
  name: 'filter',
  initialState,
  reducers: {
      showWithFilter(state, action) {
          const anecdotes = action.payload.anecdotes
          const searched = action.payload.content
          const anecdotesToShow = searched === ''
              ? anecdotes
              : anecdotes
                  .filter((anecdote) => anecdote.content
                      .toLocaleLowerCase()
                      .includes(searched
                          .toLocaleLowerCase()));
          return anecdotesToShow
      },

      filter(state) {
          return ''
      }
  }
})

export const { showAll, filter } = filterSlice.actions
export default filterSlice.reducer
