import { createSlice } from '@reduxjs/toolkit'

const initialState = null

const notificationSlice = createSlice({
  name: 'notifications',
  initialState,
  reducers: {
      setVoteNotification(state, action) {
        return action.payload
      },
      setCreateNotification(state, action) {
        return action.payload
      },
      setNullNotification(state, action) {
        return action.payload
      }
  }
})

export const { setVoteNotification, setCreateNotification, setNullNotification } = notificationSlice.actions
export default notificationSlice.reducer

export const setNotification = (message, time) => {
  return async dispatch => {
    dispatch(setVoteNotification(message))
  }
}
