import { useDispatch, useSelector } from 'react-redux'
import { showWithFilter } from '../reducers/filterReducer'

const Filter = () => {
    const dispatch = useDispatch()
    const anecdotes = useSelector(state => state.anecdotes)
    const filter = useSelector(state => state.filter)

    const handleChange = (event) => {
        event.preventDefault()
        const content = event.target.searched
        event.target.searched.value = ''
        dispatch(showWithFilter(content, anecdotes))          
    }
    const style = {
      marginBottom: 10
    }

    return (
      <div style={style}>
        filter <input name='searched' onChange={handleChange} />
      </div>
    )
  }

  export default Filter
