const initialState = {
  good: 0,
  ok: 0,
  bad: 0
}

const counterReducer = (state = initialState, action) => {
  console.log(action)
  switch (action.type) {
    case 'GOOD':
      const changedGood = state.good +1
      const newState = {...state, good: changedGood}
      return newState
    case 'OK':
      return state
    case 'BAD':
      return state
    case 'ZERO':
      return state
    default: return initialState
  }

}

export default counterReducer
